<?phpnamespace Boostify_Size_Guide\Widgets;use Boostify_Size_Guide\Base_Widget;use Elementor\Controls_Manager;use Elementor\Group_Control_Typography;use Elementor\Group_Control_Border;use Elementor\Group_Control_Background;use Elementor\Scheme_Typography;/** * Size Guide * * Elementor widget for Site Search. */class Size_Guide extends Base_Widget {    /**     * Retrieve the widget name.     *     * @return string Widget name.     */    public function get_name() {        return 'ht-hf-size-guide';    }    /**     * Retrieve the widget title.     *     * @return string Widget title.     */    public function get_title() {        return esc_html__( 'Size Guide', 'boostify' );    }    /**     * Retrieve the widget icon.     *     * @return string Widget icon.     */    public function get_icon() {        return 'eicon-pojome';    }    public function get_script_depends() {        return array( 'boostify_hf_size_guide' );    }    /**     * Register the widget controls.     *     * Adds different input fields to allow the user to change and customize the widget settings.     */    protected function _register_controls() { // phpcs:ignore        $this->start_controls_section(            'section_content',            array(                'label' => esc_html__( 'Size Guide', 'boostify' ),            )        );        $this->add_control(            'text',            array(                'label' => __( 'Text', 'boostify' ),                'type'  => Controls_Manager::TEXT,            )        );        $this->end_controls_section();        $this->start_controls_section(            'section_style',            array(                'label' => esc_html__( 'Style', 'boostify' ),                'tab'   => Controls_Manager::TAB_STYLE,            )        );        $this->add_control(            'title_color',            array(                'label'     => __( 'Text Color', 'boostify' ),                'type'      => Controls_Manager::COLOR,                'selectors' => array(                    '{{WRAPPER}} a.btn-size-guide' => 'color: {{VALUE}};',                ),            )        );        $this->add_group_control(            Group_Control_Typography::get_type(),            array(                'name'     => 'content_typography',                'label'    => __( 'Typography', 'boostify' ),                'selector' => '{{WRAPPER}} a.btn-size-guide',            )        );        $this->end_controls_section();    }    /**     * Render the widget output on the frontend.     *     * Written in PHP and used to generate the final HTML.     */    protected function render() {        $settings    = $this->get_settings_for_display();        $args = array(            'post_type'      => 'btf_builder',            'posts_per_page' => 1,            'post_status'    => 'publish',        );        $query = new \WP_Query( $args );        ?>        <div class="boostify-size-guide-popup">            <a href="#" class="btn-size-guide">                <?php                if ( ! $settings['text'] ) {                    echo esc_html( 'Size Guide', 'miini' );                } else {                    echo esc_html( $settings['text'] );                }                ?>            </a>            <div class="cd-popup">                <div class="cd-popup-container">                    <?php                    while ( $query->have_posts() ) {                        $query->the_post();                        the_content();                    }                    wp_reset_postdata();                    ?>                    <a href="#0" class="cd-popup-close"></a>                </div> <!-- cd-popup-container -->            </div> <!-- cd-popup -->        </div>        <?php    }}